import { useQuery } from '@apollo/client';

function BookDetails({bookId}) {

    const { loading, error, data } = useQuery(getBookQuery, {
        variables: { id: { bookId } },
    });
    if (loading) return null;
    if (error) return `Error! ${error}`;

    function displayBookDetails() {

        console.log({ bookId });
